var kie=function(){var e=void 0,o=document;this.cookie=function(n,i,t){if(i!=e)if(t!=e){var c=new Date;c.setTime(c.getTime()+24*t.expires*60*60*1e3),o.cookie=encodeURIComponent(n)+"="+encodeURIComponent(i)+";expires="+c.toGMTString()}else o.cookie=encodeURIComponent(n)+"="+encodeURIComponent(i);else for(var t=o.cookie.split("; "),r=0;r<t.length;r++){var p=t[r].split("=");if(p[0]==decodeURIComponent(n))return decodeURIComponent(p[1])}}},yl=new kie;kie.prototype.removeCookie=function(e){yl.cookie(e,"",{expires:-1})};